import React, { useState } from 'react';
import './CompanyDashboard.css';
import { FaBell } from 'react-icons/fa';

function CompanyDashboard() {
  const [activeSection, setActiveSection] = useState('search');
  const [activeTab, setActiveTab] = useState('all');

  const allInternships = [
    {
      title: 'Frontend Developer Intern',
      company: 'TechCorp',
      majors: 'All Engineering Students',
      date: '02/05/2025',
      duration: '3 months',
      status: 'Active'
    },
    {
      title: 'AI Intern',
      company: 'DataLab Inc.',
      majors: 'AI, Data Science',
      date: '03/05/2025',
      duration: '4 months',
      status: 'Active'
    },
    {
      title: 'Pharma Sales Intern',
      company: 'HealthX',
      majors: 'Pharmacy, Management',
      date: '01/05/2025',
      duration: '3 months',
      status: 'Closed'
    },
    {
      title: 'Mobile Dev Intern',
      company: 'InnoApps',
      majors: 'All Engineering Students',
      date: '04/05/2025',
      duration: '6 months',
      status: 'Active'
    }
  ];

  const myInternships = [
    {
      title: 'Frontend Developer Intern',
      applications: 12,
      newApps: 3,
      status: 'Active',
      duration: '3 months',
      date: '02/05/2025'
    },
    {
      title: 'UI/UX Designer Intern',
      applications: 5,
      newApps: 1,
      status: 'Closed',
      duration: '2 months',
      date: '28/04/2025'
    }
  ];

  const renderContent = () => {
    if (activeSection === 'search') {
      return (
        <div className="internship-section animated fadeInUp">
          <div className="tab-buttons slide-in-left">
            <button className={activeTab === 'my' ? 'active' : ''} onClick={() => setActiveTab('my')}>My Internship Postings</button>
            <button className={activeTab === 'all' ? 'active' : ''} onClick={() => setActiveTab('all')}>All Internship Postings</button>
          </div>

          <div className="filter-bar fade-in-delayed">
            <input type="text" placeholder="Search internships..." />
            <select>
              <option value="">Filter by major</option>
              <option value="engineering">Engineering</option>
              <option value="pharmacy">Pharmacy</option>
              <option value="management">Management</option>
            </select>
            <select>
              <option value="recent">Sort: Recent</option>
              <option value="oldest">Sort: Oldest</option>
            </select>
          </div>

          <div className="table-container animated fadeInUp">
            <table className="internship-table">
              <thead>
                <tr>
                  <th>Title</th>
                  {activeTab === 'all' && <th>Company</th>}
                  {activeTab === 'my' && <th>Applications</th>}
                  <th>Majors</th>
                  <th>Duration</th>
                  <th>Status</th>
                  <th>Posted Date</th>
                </tr>
              </thead>
              <tbody>
                {activeTab === 'all'
                  ? allInternships.map((item, idx) => (
                      <tr key={idx} className="pop-in delay-0">
                        <td className="intern-link">{item.title}</td>
                        <td>{item.company}</td>
                        <td>{item.majors}</td>
                        <td>{item.duration}</td>
                        <td className={`status-tag ${item.status.toLowerCase()}`}>{item.status}</td>
                        <td>{item.date}</td>
                      </tr>
                    ))
                  : myInternships.map((item, idx) => (
                      <tr key={idx} className="pop-in delay-0">
                        <td className="intern-link">{item.title}</td>
                        <td>
                          {item.applications}
                          {item.newApps > 0 && <span className="badge">({item.newApps} new)</span>}
                        </td>
                        <td>All Engineering Students</td>
                        <td>{item.duration}</td>
                        <td className={`status-tag ${item.status.toLowerCase()}`}>{item.status}</td>
                        <td>{item.date}</td>
                      </tr>
                    ))}
              </tbody>
            </table>
          </div>
        </div>
      );
    }

    return <h2 className="animated fadeIn">Loading...</h2>;
  };

  return (
    <div className="dashboard-layout">
      <aside className="sidebar">
        <div className="logo">
          <img src="/images/guc-logo.png" alt="GUC Logo" className="logo-img" />
          <div className="logo-text"><span className="tagline">GUC Internships</span></div>
        </div>
        <ul>
          <li className={activeSection === 'search' ? 'active' : ''} onClick={() => setActiveSection('search')}>Search Internships</li>
        </ul>
      </aside>

      <main className="main-content">
        <div className="floating-notif"><FaBell className="wiggle-bell" /></div>
        <section className="hero-banner animated fadeSlideUp">
          <h2>Welcome back, TechCorp ðŸ‘‹</h2>
          <p className="subtext">
            Today is {new Date().toLocaleDateString('en-US', {
              weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'
            })}
          </p>
        </section>

        <section className="content-area">{renderContent()}</section>
      </main>
    </div>
  );
}

export default CompanyDashboard;
