import React, { useState } from 'react';
import './CompanyDashboard.css';
import { FaTh, FaSearch, FaFileAlt, FaComments, FaChartBar, FaNewspaper } from 'react-icons/fa';

function CompanyDashboard() {
  const [activeSection, setActiveSection] = useState('dashboard');

  const renderContent = () => {
    switch (activeSection) {
      case 'dashboard':
        return <h2>Dashboard Overview</h2>;
      case 'search':
        return <h2>Search Internships</h2>;
      case 'applications':
        return <h2>Applications</h2>;
      case 'messages':
        return <h2>Messages</h2>;
      case 'statistics':
        return <h2>Statistics</h2>;
      case 'news':
        return <h2>Company News</h2>;
      default:
        return null;
    }
  };

  return (
    <div className="dashboard-layout">
      <aside className="sidebar">
        <div className="logo">GUC</div>
        <ul>
          <li className={activeSection === 'dashboard' ? 'active' : ''} onClick={() => setActiveSection('dashboard')}>
            <FaTh /> Dashboard
          </li>
          <li className={activeSection === 'search' ? 'active' : ''} onClick={() => setActiveSection('search')}>
            <FaSearch /> Search Internships
          </li>
          <li className={activeSection === 'applications' ? 'active' : ''} onClick={() => setActiveSection('applications')}>
            <FaFileAlt /> Applications
          </li>
          <li className={activeSection === 'messages' ? 'active' : ''} onClick={() => setActiveSection('messages')}>
            <FaComments /> Messages
          </li>
          <li className={activeSection === 'statistics' ? 'active' : ''} onClick={() => setActiveSection('statistics')}>
            <FaChartBar /> Statistics
          </li>
          <li className={activeSection === 'news' ? 'active' : ''} onClick={() => setActiveSection('news')}>
            <FaNewspaper /> News
          </li>
        </ul>
      </aside>

      <main className="main-content">
        <header className="main-header">
          <div className="header-title">Welcome back, Example Corp</div>
          <div className="header-tools">
            {/* Placeholder for profile image or settings */}
            <img src="https://via.placeholder.com/40" alt="Profile" className="profile-img" />
          </div>
        </header>
        <section className="content-area">{renderContent()}</section>
      </main>
    </div>
  );
}

export default CompanyDashboard;
